import React from "react";
import './Calendar.css';

class Calendar extends React.Component {
constructor(props) {
        super(props);
        this.state = {value: ''};

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    render() {
        return (
            <div className="progres-calendar-wrapper">
                <div className="calendar">
                    <div className="nameMonthWrapper">
                        <div className="leftArrow arrow">
                            <svg width="8" height="12" viewBox="0 0 8 12" fill="none"
                                 xmlns="http://www.w3.org/2000/svg">
                                <path d="M7.41 10.59L2.83 6L7.41 1.41L6 0L0 6L6 12L7.41 10.59Z" fill="black"
                                      fill-opacity="0.54"/>
                            </svg>
                        </div>
                        <div className="nameMonth">May 2019</div>
                        <div className="rightArrow arrow">
                            <svg width="8" height="12" viewBox="0 0 8 12" fill="none"
                                 xmlns="http://www.w3.org/2000/svg">
                                <path d="M0.589996 10.59L5.17 6L0.589996 1.41L2 0L8 6L2 12L0.589996 10.59Z" fill="black"
                                      fill-opacity="0.54"/>
                            </svg>
                        </div>
                    </div>

                    <div className="monthDayWrapper tableBody">
                        <div className="nameDayWrapper">
                            <div className="nameDay">Mon</div>
                            <div className="nameDay">Tue</div>
                            <div className="nameDay">Wed</div>
                            <div className="nameDay">Thu</div>
                            <div className="nameDay">Fri</div>
                            <div className="nameDay">Sat</div>
                            <div className="nameDay">Sun</div>
                        </div>
                        <div className="days"></div>
                    </div>

                </div>
            </div>
        );
    }
}

const datePicker = () => {
    const datePicker = document.querySelector('.calendar');
    const dateElement = document.querySelector('.nameMonthWrapper');
    const mthElements = document.querySelector('.nameMonth');
    const nextMthElement = document.querySelector('.rightArrow');
    const preMthElement = document.querySelector('.leftArrow');
    const daysElements = document.querySelector('.days');

    const months = ['January', 'February', 'March',
        'April', 'May', 'June',
        'July', 'August', 'September',
        'October', 'November', 'December'];

    let date = new Date();
    let day = date.getDate();
    let month = date.getMonth();
    let year = date.getFullYear();

    const selectedDay = day;
    const selectedMonth = month;
    const selectedYear = year;

    mthElements.textContent = `${months[month]} ${year}`;
    countDayMonth();

    datePicker.addEventListener('click', toggleDatePicker);
    nextMthElement.addEventListener('click', toggleNextMonth);
    preMthElement.addEventListener('click', togglePreMonth);

    const toggleDatePicker = (e) => {
        dateElement.classList.toggle( 'active');
    }

    const toggleNextMonth = (e) => {
        month++;
        if ( month > 11 ) {
            month = 0;
            year++;
        }
        mthElements.textContent = months[month] + ' ' + year;
        countDayMonth();
    }

    const togglePreMonth = (e) => {
        month--;
        if ( month < 0 ) {
            month = 11;
            year--;
        }
        mthElements.textContent = months[month] + ' ' + year;
        countDayMonth();
    }

    const countDayMonth = (e) => {
        daysElements.innerHTML = '';
        let i = 0;
        let countDays = 31;

        if (month % 2 !== 0 ) {
            countDays = 30;
        }
        if (month === 1) countDays = 28;
        if (month === 11) countDays = 31;


        while ( i < countDays) {
            const dayElement = document.createElement('div');
            dayElement.classList.add('day');
            dayElement.textContent = i + 1;

            if ( selectedDay === (i + 1) && selectedYear === year && selectedMonth === month ) {
                dayElement.classList.add('blue');
            }
            daysElements.appendChild(dayElement);
            i++;
        }
    }
}


export default Calendar;
